INSERT INTO `logs` VALUES ('b2fb9624-9d2c-4478-a947-5300fa6a5ffb', 'http://localhost/getGoldenInfoById', 'POST', '0:0:0:0:0:0:0:1', 'com.golden.website.controller.ManageController.getGoldenInfoById', '[Ljava.lang.Object;@6de5e2f', '{\"id\":\"7d4189eb-d706-40f5-a656-debc017f41d5\", \"title\":\"国华能源生产管理信息系统\", \"content\":\"<div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所属行业：风力发电</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;客户名称：国华能源投资有限公司</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;客户背景介绍：</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 国华能源投资有限公司于1998年经国务院领导批示，由原国家计委以煤代油资金办公室改制而成，隶属于中央特大型能源企业——神华集团，主要任务是进行以风力发电为主的可再生能源投资开发以及管理和回收煤代油资金。截止到2014年6月底，公司拥有独资、控股子公司54家，年营业收入达55.7亿元，管理总资产超过600亿元，已并网运行风电场及光伏电站45个，并网容量511万千瓦。</div><div>项目介绍：</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 国华能源投资有限公司数据中心项目是由国华集团信息中心牵头打造的包含全公司所有新能源电站接入的数据中心系统和多个区域集中监控系统项目。项目包含北京总部生产管理系统、河北赤沽集控中心、山东集控中心、河北尚义集控中心的建设和所有电站的接入。</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 项目由北京岳能科技公司于2015年承建，实现现场不同监控系统、不同风机机型监控的统一接入、统一监视和统一控制。基于数据中心大数据分析的基础上，实现运维策略优化。通过对实时数据库累计收集的风机各个设备、零部件的实时/历史数据、故障数据、气候数据等进行检测、分析和挖掘，得到设备发生异常、故障或损坏的概率与设备品牌、使用年限、运行状态以及外部条件等因素的关系，为现场工作人员提前维修、更换或是准备备件提供数据支持。</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 由于风电场/光伏电站地处偏远且分布分散，为了标准化统一管理，及时有效的掌握生产信息，国华投资公司建立了一个由总部数据应用中心、分公司数据应用中心及电场生产管理组成的生产管理信息系统，在山东新户区域建设以新户风电场为中心的集控中心，在河北赤城沽源地区建设以恒泰升压站为中心的集控中心。</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 整个系统基于庚顿实时数据库构建，分别在总部和各分公司、区域集控中心部署庚顿实时数据库，使用庚顿实时数据库的标准采集接口完成现场数据的实时采集、存储，通过庚顿实时数据库强大的镜像功能向集团总部实时汇聚传输全量数据，实现数据由分公司到区域集控中心再到北京数据中心的层级递进汇总，打造了一套覆盖全集团公司的分布式数据库网络。目前该项目已完成并投入运行，获得客户高度认可。</div>\", \"menu\":\"35\", \"module\":\"3\", \"imgurl\":\"null\", \"enumvaluemenu\":\"null\", \"enumvaluemodule\":\"null\", \"createtime\":\"Wed Dec 26 17:25:53 CST 2018\"}', '2019-01-28 17:34:01', '61');
